import React, { useEffect, useState } from "react";
import { getProducts } from "../api";

function ProductList() {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    setLoading(true);
    getProducts()
      .then(setProducts)
      .catch(err => setError(err.message || "Error loading products"))
      .finally(() => setLoading(false));
  }, []);

  return (
    <div>
      <h1 className="text-2xl font-bold mb-4">Products</h1>
      {loading && <div className="text-gray-500">Loading...</div>}
      {error && <div className="text-red-600">{error}</div>}
      {!loading && !error && (
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          {products.length === 0 ? (
            <div className="col-span-full text-gray-400">No products found.</div>
          ) : (
            products.map(product => (
              <div key={product.id} className="bg-white rounded shadow p-4 flex flex-col">
                <div className="font-semibold text-lg mb-2">{product.name}</div>
                <div className="text-gray-700 mb-2">${product.price.toFixed(2)}</div>
                <button className="mt-auto bg-blue-600 text-white px-3 py-1 rounded hover:bg-blue-700">Add to Cart</button>
              </div>
            ))
          )}
        </div>
      )}
    </div>
  );
}

export default ProductList;
